#include "../headers/vector.hpp"

Vector2::Vector2(float newX, float newY)
{
    X = newX;
    Y = newY;
}

int2::int2(int newX, int newY)
{
    X = newX;
    Y = newY;
}

Vector3::Vector3(float newX, float newY, float newZ)
{
    X = newX;
    Y = newY;
    Z = newZ;
}

int3::int3(int newX, int newY, int newZ)
{
    X = newX;
    Y = newY;
    Z = newZ;
}